import { Component, OnInit } from '@angular/core';
import { Drink } from 'src/app/_core/models/drink.model';
import { getAllDrinks, getSearchedDrinks } from 'src/app/_core/services';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent implements OnInit {
  drinks: Drink[] = [];

  ngOnInit(): void {
    this.setAllDrinks();
  }

  async setAllDrinks()  {
    await getAllDrinks().then((res: any) => 
     {
       this.drinks = res;
      console.log(this.drinks);}
    ).catch((e:any )=> console.log(e));
  }

  async setSearchedDrinks()  {
    await getSearchedDrinks().then((res: any) => 
     {
       this.drinks = res;
      console.log(this.drinks);}
    ).catch((e:any )=> console.log(e));
  }

}
<div class="search-box">
  <button class="btn-search">Search</button>
  <input type="text" class="input-search" placeholder="Type to Search..." />
</div>

<div>
  <div *ngFor="let drink of drinks">
    <app-card [drink]="drink"></app-card>
  </div>
</div>
