import axios from "axios";
import { Drink } from "../models/drink.model";

const BASE_URL = "www.thecocktaildb.com/api/json/v1/1";
export const getAllDrinks = async ()=> {
    const response = axios.get(`${BASE_URL}/search.php?s=`).then((res) => {
        const data: Drink = res.data;
        return data;
    })
    return response;
}

export const getSearchedDrinks = async (name: string)=> {
    const response = axios.get(`${BASE_URL}/search.php?s=${name}`).then((res) => {
        const data: Drink = res.data;
        return data;
    })
    return response;
}

export const getDrinkDetailById = async (id:string) => {
    const response = axios.get(`${BASE_URL}/lookup.php?i=${id}`).then((res) => {
        const data: Detail = res.data;
        return data;
    })
    return response;
}
