import { Component, Input } from '@angular/core';
import { Drink } from 'src/app/_core/models/drink.model';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
})
export class CardComponent {
  @Input() drink: Drink = {
    idDrink: '',
    strDrink: ''
  }
}
<div [routerLink]="'/drink/' + drink.idDrink">
  <b>{{ drink.strDrink }}</b>
  <p>{{ drink.idDrink }}</p>
</div>
