import { Component, OnInit } from '@angular/core';
import { TitleStrategy } from '@angular/router';
import { Drink } from 'src/app/_core/models/drink.model';
import { getAllDrinks, getSearchedDrinks } from 'src/app/_core/services';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent implements OnInit {
  drinks: Drink[] = [];
  searchedDrink:string = '';

  ngOnInit(): void {
    this.setAllDrinks();
  }

  async setAllDrinks()  {
    await getAllDrinks().then((res: any) => 
     {
       this.drinks = res;}
    ).catch((e:any )=> console.log(e));
  }

  async setSearchedDrinks()  {
    await getSearchedDrinks(this.searchedDrink).then((res: any) => 
     {
       this.drinks = res;}
    ).catch((e:any )=> console.log(e));
  }

  clearSearch() {
    this.searchedDrink = ''
    this.setAllDrinks()
  }
}
<div class="search-box">
  <button class="btn-search" (click)="setSearchedDrinks()">Search</button>
  <input
    type="text"
    class="input-search"
    placeholder="Type to Search..."
    [(ngModel)]="searchedDrink"
    (keyup.enter)="setSearchedDrinks()"
  />

  <button class="btn-search" (click)="clearSearch()">Clear</button>
</div>

<div>
  <div *ngFor="let drink of drinks">
    <app-card [drink]="drink"></app-card>
  </div>
</div>
